            //document.write('<a href="' + posturl + '" target ="_top"><img  class="label-thumb"  src="' + thumburl + '"/></a>');
        //document.write('<strong class="label-title"><a  href="' + posturl + '" target  ="_top">' + posttitle + '</a></strong><br>');
        postsHTML += '<strong class="label-title"><a  href="' + posturl + '" target  ="_top">' + posttitle + '</a></strong><br>';
        if ("content" in entry) {
            var postcontent = entry.content.$t;
        } else
        if ("summary" in entry) {
