                document.write(postcontent);
                document.write('');*/
                postsHTML += postcontent
            } else {
                //document.write('');
                postcontent = postcontent.substring(0, numchars);
                var quoteEnd = postcontent.lastIndexOf("  ");
                postcontent = postcontent.substring(0, quoteEnd);
                //document.write(postcontent + '...');
                //document.write('');
                postsHTML += postcontent + "..."
            }
        }
        var towrite = '';
        var flag = 0;
        //document.write('<br>');
        postsHTML += '<br>';
        if (displaymore == true) {
            if (flag == 1) towrite = towrite + ' | ';
            towrite = towrite + '<a  href="' + posturl + '" class="url" target ="_top">More  »</a>';
            flag = 1;;
        }
        /*document.write(towrite);
        document.write('</li>');*/
        postsHTML += towrite;
        postsHTML += '</li>';
        /*if (displayseparator == true)
            if (i != (numposts - 1))
                document.write('');*/
    }
    document.getElementById("label_with_thumbs").innerHTML = postsHTML;
    /*document.write('</ul>');*/
