      function UpdateDarkMode(){
          if(darkmode_enabled){
                DarkReader.setFetchMethod(window.fetch)
                DarkReader.enable({
                    brightness: 100,
                    contrast: 90,
                    sepia: 10
				});
                document.body.setAttribute("data-darkmode", "");
          }
          else {
			  DarkReader.disable();
              document.body.removeAttribute("data-darkmode")
		  }
	  }
