		//https://stackoverflow.com/questions/74566397/get-previous-and-next-post-titles

        /*********** Between Posts Navigation Starts ************/
        var olderLink = document.querySelector('#Blog1_blog-pager-older-link');
        var newerLink = document.querySelector('#Blog1_blog-pager-newer-link');
        // set the page title from feed data
        function setPageTitleAndThumb(data, pageLink, prefix, suffix) {
          if (data.feed.entry) {
            if (data.feed.entry.length > 0) {
              var title = data.feed.entry[0].title.$t;
            }
			if(data.feed.entry[0].media$thumbnail){
			  var thumb = data.feed.entry[0].media$thumbnail.url;
			}
          }
          if (title) {
            pageLink.querySelector("h5").innerHTML = prefix + title + suffix;
          }
          if (thumb) {
            pageLink.querySelector("img").src = thumb;
          }
        }

        // get entry data from the feed
        function getPageTitle(pageLink, callback) {
          var pathname = pageLink.getAttribute('href').replace(location.protocol + '//' + location.hostname, '');
		  loadScript('/feeds/posts/summary?alt=json-in-script&max-results=1&redirect=false&path=' + pathname + '&callback=' + callback.name, function(){});
        }

        function setOlderPageTitle(data) {
          setPageTitleAndThumb(data, olderLink, '', '')
        };

        function setNewerPageTitle(data) {
          setPageTitleAndThumb(data, newerLink, '', '')
        };
		window.addEventListener('load', function(){
          if (olderLink) {
            getPageTitle(olderLink, setOlderPageTitle);
          }
          if (newerLink) {
            getPageTitle(newerLink, setNewerPageTitle);
          }
		}, { once: true });
