function UseWidgetManager(){
  _WidgetManager._Init('//www.blogger.com/rearrange?blogID\x3d3822370711802143985','//al-ehyaa.blogspot.com/2019/02/blog-post_22.html','3822370711802143985');

  _WidgetManager._RegisterWidget('_BlogView', new _WidgetInfo('Blog1', 'post', document.getElementById('Blog1'), {'cmtInteractionsEnabled': false, 'lightboxEnabled': true, 'lightboxModuleUrl': 'https://www.blogger.com/static/v1/jsbin/1495742447-lbx__ar.js'}, 'displayModeFull'));
}
const UseWidgetManagerInterval_timeout = 5000;
const UseWidgetManagerInterval_delay = 500;
var UseWidgetManagerInterval_time = 0;
var UseWidgetManagerInterval = setInterval(function(){
		if(_WidgetManager){
			clearInterval(UseWidgetManagerInterval);
			UseWidgetManager()
		}
		if (UseWidgetManagerInterval_time >= UseWidgetManagerInterval_timeout){
			clearInterval(UseWidgetManagerInterval);
		}
		UseWidgetManagerInterval_time += UseWidgetManagerInterval_delay;
	}, UseWidgetManagerInterval_delay);


